import { useState, useEffect, useRef } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import { useAuth } from '@/context/AuthContext';
import type { User, Message } from '@shared/schema';

interface Conversation {
  user: User;
  lastMessage: Message;
  unreadCount: number;
}

interface Story {
  id: string;
  userId: string;
  userName: string;
  userAvatar: string;
  imageUrl: string;
  text?: string;
  timestamp: Date;
  viewed: boolean;
}

interface FriendWithStory {
  id: string;
  name: string;
  avatar: string;
  hasStory: boolean;
  gender?: 'male' | 'female';
  stories?: Story[];
}

export default function ChatPage() {
  const [selectedUser, setSelectedUser] = useState<User | null>(null);
  const [selectedConversation, setSelectedConversation] = useState<Conversation | null>(null);
  const [messageInput, setMessageInput] = useState('');
  const [isEmojiPickerOpen, setIsEmojiPickerOpen] = useState(false);
  const [isAttachmentMenuOpen, setIsAttachmentMenuOpen] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  // Story viewer state
  const [selectedStory, setSelectedStory] = useState<FriendWithStory | null>(null);
  const [currentStoryIndex, setCurrentStoryIndex] = useState(0);
  const [storyProgress, setStoryProgress] = useState(0);
  const [friendsWithStoriesState, setFriendsWithStoriesState] = useState<FriendWithStory[]>([]);
  const { user: currentUser } = useAuth();
  const queryClient = useQueryClient();

  // Initialize friendsWithStoriesState with mock data
  useEffect(() => {
    setFriendsWithStoriesState([
    {
      id: '1',
